mapper.update:
update sky_take_out.setmeal
name = #{name}, price = #{price}, description = #{description}, image = #{image}, category_id = #{categoryId}, status = #{status},
where id = #{id}

mapper.select:
select s.*,c.name categoryName from sky_take_out.setmeal s
    left join sky_take_out.category c on s.category_id = c.id

     and name like concat('%',#{name},'%') 
     and category_id = #{categoryId}
     and status = #{status}

order by s.create_time desc

select * from sky_take_out.setmeal


        and name like concat('%',#{name},'%')


        and category_id = #{categoryId}


        and status = #{status}

select count(id) from sky_take_out.setmeal


        and status = #{status}


        and category_id = #{categoryId}
